    def writeAString(self, string, inverse=False, font=Q5Font):
        """
        takes a string and writes it to the screen 
        various niceties include centering the words on the screen 
        and splitting words over lines if there are more than one 
        """
        self.clearScreen()
        sleep(0.1)
        string = string[:32] # can't write more than 32 chars...
        totalWords = 1
        totalChars = len(string)
        if ' ' in string:
            # split the words apart and count
            wordsToWrite = string.split(' ')
            totalWords = len(wordsToWrite)
        else:
            wordsToWrite = [string]

        if totalWords == 1:
            totalChars = len(wordsToWrite[0])
            if totalChars > 8: # start the word at line 1 so that it wraps
                position = 1
            else:
                position = 9
                cursorMove = 8 - totalChars
                position += (cursorMove/2)
            for char in wordsToWrite[0]:
                curs = 'Curs'+str(position)
                self.write_char_to_display('Char_'+char, curs, inverse=inverse, font=font)
                
                position += 1
        else:
            position = 1
            rows = [9, 17, 25]
            for word in wordsToWrite:
                if position < 9:
                    position = 9
                elif position < 17:
                    position = 17
                elif position < 25:
                    position = 25
                for char in word:
                    curs = 'Curs'+str(position)
                    self.write_char_to_display('Char_'+char, curs, inverse=inverse, font=font)
                    
                    position += 1
    
    def testStringWriting(self):
        """
        given the supported characters try to write a string to the display
        -- only numbers and the letter D
        """
        self.write_0x49_BMP(self.BMP_COMMANDS['CLEARSCREEN'])
        position = 1
        for i in '1234567890D':
            if i != 'D':
                char_to_write = 'Num_'+i
            else:
                char_to_write = 'Char_'+i
            curs = 'Curs'+str(position)
            position += 1
            self.write_char_to_display(char_to_write, curs)
        return
        
        
    def testSPI(self):
        """
        this is a never ending loop of changing the colors and 
        writing BMPs to test the SPI bus and for troubleshooting
        """
        while 1:
            for color in self.COLORS:
                self.changeColor(color)
                sleep(0.25)
            self.changeColor('RED')
            for symbol, value in self.BMP_COMMANDS.items():
                self.write_0x49_BMP(value)
                sleep(0.25)

